          placeholder="PPB Number"
          className="input"
          {...register("ppbNumber")}
        />
        <p className="error-text">{errors.ppbNumber?.message}</p>

        <input
          type="file"
          accept="image/*,application/pdf"
          {...register("ppbFile")}
        />
        <p className="error-text">{errors.ppbFile?.message}</p>
      </>
    )}
  </div>
)}

             <div className="btn-group">
  {currentStep === 0 ? (
    <button
      type="button"
      onClick={async () => {
        const isValid = await trigger();
        if (isValid) setCurrentStep(currentStep + 1);
      }}
    >
      Next
    </button>
  ) : currentStep === totalSteps - 1 ? (
    <>
      <button type="button" onClick={() => setCurrentStep(currentStep - 1)}>
        Previous
      </button>
      <button
        type="button"
        onClick={async () => {
          const isValid = await trigger();
          if (isValid) {
            await handleSubmit(onSubmit)();
          }
        }}
      >
        Submit
      </button>
    </>
  ) : (
    <>
      <button type="button" onClick={() => setCurrentStep(currentStep - 1)}>
        Previous
      </button>
      <button
        type="button"
        onClick={async () => {
          const isValid = await trigger();
          if (isValid) setCurrentStep(currentStep + 1);
        }}
      >
        Next
      </button>
    </>
  )}
</div>
 
 
 
          </form>
        </FormProvider>
      </div>
      <div className="form-right" style={{ position: 'relative' }}>
        <button
          type="button"
          onClick={() => navigate('/dashboard')}
          style={{
            position: 'absolute',
            top: 12,
            right: 12,
            background: 'rgba(0,0,0,0.45)',
            color: '#fff',
            border: 'none',
            borderRadius: '50%',
            width: 32,
            height: 32,
            cursor: 'pointer',
            fontWeight: 700,
            fontSize: 20,
            lineHeight: '32px',
            textAlign: 'center',
            zIndex: 2
          }}
          aria-label="Close and go to dashboard"
        >
          ×
        </button>
        <img src={farmImage} alt="Farm Field" className="form-image" />
      </div>
    </div>
    

  );
};
  export default FarmerForm;
