import React, { useState } from 'react';
import '../styles/RegistrationApproval.css';

const RegistrationApprovalModal = ({ 
  isOpen, 
  onClose, 
  registration, 
  onApprove, 
  onReject 
}) => {
  const [rejectionReason, setRejectionReason] = useState('');
  const [selectedRole, setSelectedRole] = useState(registration?.role || 'FARMER');
  const [isSubmitting, setIsSubmitting] = useState(false);

  if (!isOpen || !registration) return null;

  // Debug logging
  console.log('RegistrationApprovalModal - registration:', registration);
  console.log('RegistrationApprovalModal - status:', registration.status);

  const handleApprove = async () => {
    setIsSubmitting(true);
    try {
      console.log('Approving registration with role:', selectedRole);
      await onApprove(registration.id, selectedRole);
      onClose();
    } catch (error) {
      console.error('Error approving registration:', error);
      alert('Failed to approve registration. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleReject = async () => {
    if (!rejectionReason.trim()) {
      alert('Please provide a reason for rejection.');
      return;
    }

    setIsSubmitting(true);
    try {
      await onReject(registration.id, rejectionReason);
      onClose();
    } catch (error) {
      console.error('Error rejecting registration:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const getStatusBadge = (status) => {
    const statusColors = {
      'PENDING': 'status-pending',
      'APPROVED': 'status-approved',
      'REJECTED': 'status-rejected'
    };
    
    return (
      <span className={`status-badge ${statusColors[status] || 'status-pending'}`}>
        {status}
      </span>
    );
  };

  const getRoleBadge = (role) => {
    const roleColors = {
      'FARMER': 'role-farmer',
      'EMPLOYEE': 'role-employee',
      'ADMIN': 'role-admin',
      'SUPER_ADMIN': 'role-super-admin'
    };
    
    return (
      <span className={`role-badge ${roleColors[role] || 'role-default'}`}>
        {role}
      </span>
    );
  };

  return (
    <div className="registration-approval-modal">
      <div className="modal-overlay" onClick={onClose}></div>
      <div className="modal-content">
        <div className="modal-header">
          <h2>Registration Approval</h2>
          <button className="close-btn" onClick={onClose}>&times;</button>
        </div>

        <div className="modal-body">
          <div className="registration-info">
            <div className="info-row">
              <label>Registration ID:</label>
              <span>{registration.id}</span>
            </div>
            <div className="info-row">
              <label>User Type:</label>
              {getRoleBadge(registration.role)}
            </div>
            <div className="info-row">
              <label>Status:</label>
              {getStatusBadge(registration.status)}
            </div>
            <div className="info-row">
              <label>Registration Date:</label>
              <span>{new Date(registration.registrationDate || registration.dateOfBirth).toLocaleDateString()}</span>
            </div>
          </div>

          <div className="user-details">
            <h3>User Details</h3>
            <div className="details-grid">
              <div className="detail-item">
                <label>Full Name:</label>
                <span>{registration.name || `${registration.firstName} ${registration.lastName}`}</span>
              </div>
              <div className="detail-item">
                <label>Email:</label>
                <span>{registration.email}</span>
              </div>
              <div className="detail-item">
                <label>Phone:</label>
                <span>{registration.phoneNumber || registration.contactNumber}</span>
              </div>
              <div className="detail-item">
                <label>Date of Birth:</label>
                <span>{new Date(registration.dateOfBirth).toLocaleDateString()}</span>
              </div>
              <div className="detail-item">
                <label>Gender:</label>
                <span>{registration.gender}</span>
              </div>
            </div>
          </div>

          {/* Debug section */}
          <div className="debug-section" style={{ background: '#f0f0f0', padding: '10px', margin: '10px 0', borderRadius: '5px' }}>
            <h4>Debug Info:</h4>
            <p><strong>Status:</strong> {registration.status}</p>
            <p><strong>Role:</strong> {registration.role}</p>
            <p><strong>Is PENDING:</strong> {registration.status === 'PENDING' ? 'YES' : 'NO'}</p>
          </div>

          {registration.status === 'PENDING' && (
            <div className="approval-actions">
              <h3>Approval Actions</h3>
              
              <div className="role-selection">
                <label htmlFor="role-select">Assign Role:</label>
                <select
                  id="role-select"
                  value={selectedRole}
                  onChange={(e) => setSelectedRole(e.target.value)}
                  disabled={isSubmitting}
                >
                  <option value="FARMER">Farmer</option>
                  <option value="EMPLOYEE">Employee</option>
                  <option value="ADMIN">Admin</option>
                </select>
              </div>

              <div className="action-buttons">
                <button 
                  className="approve-btn"
                  onClick={handleApprove}
                  disabled={isSubmitting}
                >
                  {isSubmitting ? 'Approving...' : 'Approve Registration'}
                </button>
                <button 
                  className="reject-btn"
                  onClick={() => document.getElementById('rejection-reason').focus()}
                  disabled={isSubmitting}
                >
                  Reject Registration
                </button>
              </div>
              
              <div className="rejection-reason">
                <label htmlFor="rejection-reason">Rejection Reason (Required for rejection):</label>
                <textarea
                  id="rejection-reason"
                  value={rejectionReason}
                  onChange={(e) => setRejectionReason(e.target.value)}
                  placeholder="Please provide a reason for rejection..."
                  rows="3"
                />
                <button 
                  className="reject-confirm-btn"
                  onClick={handleReject}
                  disabled={isSubmitting || !rejectionReason.trim()}
                >
                  {isSubmitting ? 'Rejecting...' : 'Confirm Rejection'}
                </button>
              </div>
            </div>
          )}

          {/* Always show approval actions for debugging */}
          {registration.status !== 'PENDING' && (
            <div className="approval-actions">
              <h3>Registration Status: {registration.status}</h3>
              <p>This registration is {registration.status.toLowerCase()}. No actions available.</p>
            </div>
          )}

          {registration.status === 'REJECTED' && (
            <div className="rejection-info">
              <h3>Rejection Information</h3>
              <div className="info-row">
                <label>Rejected By:</label>
                <span>{registration.rejectedBy}</span>
              </div>
              <div className="info-row">
                <label>Rejection Date:</label>
                <span>{new Date(registration.rejectionDate).toLocaleDateString()}</span>
              </div>
              <div className="info-row">
                <label>Rejection Reason:</label>
                <span className="rejection-reason-text">{registration.rejectionReason}</span>
              </div>
            </div>
          )}

          {registration.status === 'APPROVED' && (
            <div className="approval-info">
              <h3>Approval Information</h3>
              <div className="info-row">
                <label>Approved By:</label>
                <span>{registration.approvedBy}</span>
              </div>
              <div className="info-row">
                <label>Approval Date:</label>
                <span>{new Date(registration.approvalDate).toLocaleDateString()}</span>
              </div>
              <div className="info-row">
                <label>Assigned Role:</label>
                {getRoleBadge(registration.role)}
              </div>
            </div>
          )}
        </div>

        <div className="modal-footer">
          <button className="close-button" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default RegistrationApprovalModal;
