import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as Yup from "yup";

// Validation schema
const schema = Yup.object().shape({
  userInput: Yup.string()
    .required("Email/Phone/ID is required")
    .test("is-valid", "Enter a valid Email or Phone or ID", (value) => {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      const phoneRegex = /^[0-9]{10}$/;
      return emailRegex.test(value) || phoneRegex.test(value) || value.length >= 6;
    }),
});

const ForgotUsername = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const onSubmit = (data) => {
    console.log("Submitted Data:", data);
    alert("Reset link has been sent to your email/phone if it exists in our records.");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-cover bg-center p-6" style={{ backgroundImage: `url('/your-bg.jpg')` }}>
      <div className="bg-white bg-opacity-80 rounded-2xl shadow-lg flex flex-col md:flex-row w-full max-w-5xl p-8">
        {/* Left side: Form */}
        <div className="w-full md:w-1/2 pr-4">
          <h2 className="text-2xl font-bold mb-4">Forgot User ID</h2>
          <p className="mb-6">
            Enter your email address, click “Forgot User Id”, and we'll send you a link to reset your user ID.
          </p>

          <form onSubmit={handleSubmit(onSubmit)}>
            <label className="block mb-1 font-medium">
              Email/Phone/ID <span className="text-red-600">*</span>
            </label>
            <input
              {...register("userInput")}
              className="w-full p-2 border rounded mb-2"
              placeholder="Enter your Email or Phone or ID"
            />
            {errors.userInput && (
              <p className="text-red-600 text-sm mb-4">{errors.userInput.message}</p>
            )}

            <button
              type="submit"
              className="bg-green-600 text-white py-2 px-6 rounded hover:bg-green-700 transition"
            >
              Reset password
            </button>
          </form>
        </div>

        {/* Right side: Illustration */}
        <div className="w-full md:w-1/2 flex items-center justify-center mt-6 md:mt-0">
          <img src="/illustration.png" alt="Forgot User Illustration" className="max-w-full h-auto rounded-lg" />
        </div>
      </div>
    </div>
  );
};

export default ForgotUsername;
